import React, { Component } from 'react';
import  {Navbar}  from './components/Navbar';
import  Header  from './components/header';
import Main from './components/main';
import Footer from './components/footer';
//import picture
import bill_ from "./picture/billbord.JPG";
import my_push from "./picture/push.JPG";
import my_apil from "./picture/apill.JPG";

class App extends Component {
    constructor(){
        this.picture=[[0,bill_],[1,my_push],[2,my_apil]],
        this.state={
            o:0
        }
    }
    // f_up=()=>{
    //     this.setState((p)=>{
    //         return{o:p.o+1}
    //     })
    // }
    // f_down=()=>{
    //     if(p=0){
    //         alert("مقدار صفر است .")
    //     }else{
    //         this.setState((p)=>{
    //             return{o:p.o-1}
    //     })}
    // }
    render() {
        return (
            <div>
             <Navbar/>
             <Header/>
          {this.picture.map((p)=><Main picture={p[1]} key={p[0]}   pol={this.state.o}/>)}
             <Footer/>
            </div>
        );
    }
}

export default App;
