import styles from "./main.module.css"
import React, { Component } from 'react';
import up from "../picture/up.png";
import down from "../picture/down.png";
class Main extends Component {
    constructor(){
        super();
        this.state={
            pull:0
        }
    }
    f_up=()=>{
        if(this.state.pull===5){
            alert("Value is 5 more than you can't choose")
           }else{
            this.setState((p)=>{
                return{pull:p.pull+1}
            })}}
        f_down=()=>{
           if(this.state.pull===0){
            alert("The value is zero, don't try")
           }else{
            this.setState((p)=>{
                return{pull:p.pull-1}
            })
           }
            
        } 
      
    
    render(){
        return (
            <div className={styles.conteiner}>
            <div className={styles.Main}>
                <img src={this.props.picture} alt='billboard'/>
               <h3>{this.props.name}</h3>
            
              
             <h4>About</h4>
             <div className={styles.cost}>
               <img src={up}  onClick={this.f_up}  alt="up"></img> 
                <p>{this.state.pull}</p>
                <img src={down} className={!this.state.pull&&styles.pp_12} onClick={this.f_down} alt="down"/>
             </div>
        </div>
          </div>
        );
    }
}

export default Main;
