import React,{ useRef, useState} from 'react';
import left from "./pic/bx-left.svg";
import Right from "./pic/bx-right.svg";
import Play from "./pic/bx-play.svg";
import Pause from "./pic/bx-pause.svg";
 import shootia from "./gooz.mp3";
 import missozam from "./pic/masozam.mp3"
const data=[
    {
        id:1,
        title:"P6Savage",
        name:"Driver By (FreeStyle)",
        poster:shootia
    },
    {
        id:2,
        title:"P6rhum",
        name:"Maria",
        poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
    },
    {
        id:3,
        title:"DIXR",
        name:"SHAYATEEN",
        poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
    },
    {
        id:4,
        title:"HipHopologist",
        name:"Capsule Albume",
        src:missozam,
        poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
    },
    {
        id:5,
        title:"GodPoori",
        name:"SHOOTIA",
        src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
        poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
    }
]

const MusicPLY = (props) => {
    const maimg=useRef()
    let played=false
    const [musicData]=useState(data);
    let [id,setid]=useState(props.match.params.id)
    let music=musicData[id-1];
    const Music=new Audio(music.src)
    
    //function
    const play_pause=()=>{
        console.log(played)
        if(played===true){
            Music.pause()
            played=false
            maimg.current.src=Play
        }else if(played===false){
            Music.play()
            played=true
            maimg.current.src=Pause
        }
    }
    const leftForward=()=>{
        
        console.log(id)
      if(id===0){
       id=5
      }
    }

    const RightForward=()=>{
        if(id===5){
            id=0
          }else{
           id+=1
          }
    }
    return (
        <>
            <div>
                <img src={musicData[id-1].poster} alt='music'/>
                <div style={{display:"flex"}}>
                <img onClick={leftForward}  style={{width:"45px"}} src={left} alt='button'/>
                    <img ref={maimg} onClick={play_pause} style={{width:"45px"}} src={Play} alt='button'/>
                    <img onClick={RightForward}  style={{width:"45px"}} src={Right} alt='button'/>
                </div>
            </div>
            </>
    );
};

export default MusicPLY
