import React,{ useEffect, useState} from 'react';
import misozam from "./pic/masozam.mp3";
import pause from "./pic/bx-pause.svg" 
import play from "./pic/bx-play.svg" 
import left from "./pic/bx-left.svg" 
import right from "./pic/bx-right.svg" 
const data=[
    {
        id:1,
        title:"The-AB7",
        name:"GTA",
        src:"",
        poster:"https://i1.sndcdn.com/artworks-zNMOLtAABQUsR253-syMUBw-t500x500.jpg"
    },
    {
        id:2,
        title:"P6rhum",
        name:"Maria",
        src:"",
        poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
    },
    {
        id:3,
        title:"DIXR",
        name:"SHAYATEEN",
        src:"",
        poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
    },
    {
        id:4,
        title:"HipHopologist",
        name:"Capsule Albume (Misoozam)",
        src:misozam,
        poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
    },
    {
        id:5,
        title:"GodPoori",
        name:"SHOOTIA",
        src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
        poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
    }
]



const MusicPLY = (props) => {
 const Music=document.getElementsByTagName("audio") 
 const [Data]=useState(data);
 const [id,setID] =useState(props.match.params.id);
 const [playing,setPlaying]=useState(false)
 const [item ,setItem]=useState(Data[id-1])
 const [srcItem,setSrcItem]=useState(item.src)
 useEffect(()=>{ 
    setItem(Data[id-1])
   setSrcItem(item.src)
   Music[0].play()
 },[Music,Data,id,item.src])
 const playAudio=async()=>{
    let promise=new Promise(function MyPlay(){Music[0].play()}) ; 
await  setTimeout(promise,2000)
 await promise
 
 }
 const pauseAudio=()=>{
    Music[0].pause()
 }
 const channgeplaying=async()=>{
    if(playing===false){
       playAudio();
        setPlaying(!playing)

    }
    if(playing===true){
       pauseAudio()
        setPlaying(!playing)
    }
}
const channgeLeft=()=>{
    
    if(Number(id)===1){
         
    setPlaying(true)
    if(playing===true){
         playAudio()
        }
        setID(6) 
    }
    if(Number(id)>0){
        setPlaying(true)
        if(playing===true){
            playAudio()
        }
      
        setID(e=>e-=1)
        
    }
   
}
const channgeRight=()=>{
    if(Number(id)===5){
        setPlaying(true)
        if(playing===true){
            playAudio()
        }
        setID(0)
    }
    if(Number(id)<6){ 
        setPlaying(true)
        if(playing===true){
            playAudio()
        }
        setID(e=>e+=1)  
    }
    
   
    
}
return(<div style={{backgroundColor:"black"}}>
<audio src={srcItem}/>
               <img alt="my-music" src={item.poster} style={{margin:"1.5rem",borderRadius:"30px"}}/>
               <div style={{display:"flex" , justifyContent:"center",position:"absolute",top:"100px",left:"115px"}}>
                  <img alt="icon" onClick={channgeLeft} style={{width:"65px",opacity:"0.5" ,backgroundColor:"rgb(54, 252, 209)", borderRadius:"50%",margin:"1.5rem"}} src={left}  />

                  <img alt="icon" onClick={channgeplaying} style={{border:"5px solid black",width:"65px",backgroundColor:"rgb(54, 252, 209)",borderRadius:"50%",margin:"1.5rem"}} src={playing?pause:play}  />
                  <img alt="icon"  onClick={channgeRight}   style={{width:"65px",opacity:"0.5",backgroundColor:"rgb(54, 252, 209)",borderRadius:"50%",margin:"1.5rem"}} src={right}  />


</div>
</div>)
}

export default MusicPLY
