import React, { Component } from 'react';
import left from "./pic/bx-left.svg";
import Right from "./pic/bx-right.svg";
import Play from "./pic/bx-play.svg";
import Pause from "./pic/bx-pause.svg";

class MusicPlay extends Component {
    constructor(props){
        super();
        this.state={
            played:false
        }
        this.music=[
            {
                id:1,
                title:"P6Savage",
                name:"Driver By (FreeStyle)",
                poster:"https://i1.sndcdn.com/artworks-rvuxjdNaLj74T9TO-MyZSNQ-t500x500.jpg"
            },
            {
                id:2,
                title:"P6rhum",
                name:"Maria",
                poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
            },
            {
                id:3,
                title:"DIXR",
                name:"SHAYATEEN",
                poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
            },
            {
                id:4,
                title:"HipHopologist",
                name:"Capsule Albume",
                poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
            },
            {
                id:5,
                title:"GodPoori",
                name:"SHOOTIA",
                src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
                poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
            }
        ]
    }
   
    render() {
        const id=this.props.match.params.id;
        const music_show=this.music[id-1];
        const MUSIC=new Audio(music_show.src)
        console.log(MUSIC)
        return (<>
            <div>
                <img src={music_show.poster} alt='music'/>
                <div style={{display:"flex"}}>
                <img  style={{width:"45px"}} src={left} alt='button'/>
                    <img onClick={()=>this.played?MUSIC.play():MUSIC.pause()} style={{width:"45px"}} src={this.state.played?Play:Pause} alt='button'/>
                    <img  style={{width:"45px"}} src={Right} alt='button'/>
                </div>
            </div>
            </>
        );
    }
}

export default MusicPlay;
