import React,{ useEffect, useState} from 'react';
import misozam from "./masozam.mp3";
import GTA from "./GTA.mp3";
import Maria from "./Maria.mp3";
import SHAYATEEN from "./SHAYATEEN.mp3";

import play from "./pic/bx-play.svg" 
 
const data=[
    {
        id:1,
        title:"The-AB7",
        name:"GTA",
        src:GTA,
        poster:"https://i1.sndcdn.com/artworks-zNMOLtAABQUsR253-syMUBw-t500x500.jpg"
    },
    {
        id:2,
        title:"P6rhum",
        name:"Maria",
        src:Maria,
        poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
    },
    {
        id:3,
        title:"DIXR",
        name:"SHAYATEEN",
        src:SHAYATEEN,
        poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
    },
    {
        id:4,
        title:"HipHopologist",
        name:"Capsule Albume (Misoozam)",
        src:misozam,
        poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
    },
    {
        id:5,
        title:"GodPoori",
        name:"SHOOTIA",
        src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
        poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
    }
]



const MusicPLY = (props) => {
 let Music=document.getElementsByTagName("audio");
 const [Data]=useState(data);
 const [id] =useState(props.match.params.id)
 const [item ,setItem]=useState(Data[id-1])
 const [srcItem,setSrcItem]=useState(item.src)
 useEffect(()=>{ 
   setItem(Data[id-1])
   setSrcItem(item.src)
   console.log(Music[0].loop=true)
   const playAudio=()=>{
    Music[0].play()
   }
   playAudio()
 },[Music,Data,id,item.src])

return(<div style={{backgroundColor:"black",display:"flex"}}>
    <div style={{width:"20%"}} >
<audio src={srcItem} />
               <img alt="my-music" src={item.poster} style={{margin:"1.5rem",borderRadius:"30px"}}/>
               <div style={{display:"flex" , justifyContent:"center",position:"absolute",top:"270px",left:"210px"}}>
                  <img alt="icon"  style={{border:"5px solid black",width:"85px",opacity:"0.55",backgroundColor:"rgb(54, 252, 209)",borderRadius:"50%",margin:"1.5rem"}} src={play}  />
</div>
</div>
<div>
    {Data.forEach((e)=>{
        <div>
            
        </div>
    })}
</div>
</div>)
}

export default MusicPLY;
