import React, { Component } from 'react';
import styles from "./navbar.module.css";
import logo from "../picture/logo.JPG";
import styled from "styled-components";
import { Link } from 'react-router-dom';



//create-mydiv tag
const Mydiv=styled.div`
width: 35px;
height: 30px;
margin: 10px 10px;
position: relative;
cursor: pointer;
display: inline-block;

tarnsition:all 0.7s;
span{
    background-color:white;
    position: absolute;
    border-radius: 2px;
    transition: .3s cubic-bezier(.8, .5, .2, 1.4);
    width:100%;
    height: 4px;
    z-index:3;
  }
span:nth-child(1){
    top:0px;
    left: 0px;
    left: ${(p)=>p.status?'0px':'-100px'};
  }
span:nth-child(2){
    top:13px;
    left: 0px;
  }
span:nth-child(3){
    bottom:0px;
    left: ${(p)=>p.status?'0px':'-100px'};
  }
&:hover span:nth-child(1){
    transform: scaleX(1.3);
  }
  &:hover span:nth-child(3){
    transform: scaleX(.8);
  }
  @media (min-width:950px) {
        display: none;
  }
  ul{
    width:100vw;
    background-color: rgb(0, 0, 0);
    list-style: none;
    position: absolute;
    left: -87px;
    z-index:0;
    bottom: ${(p)=>p.status?'165px':'-285px'};
    text-align: center;
    transition: all 0.5s;
  
}
ul li{
    padding: 25px;
    color: #FFF;
    transition: all 0.8s;
    z-index:1;
}
ul li:hover{
  
    background-color: #fff;
    color: #000000;
}
`;
class Navbar extends Component {
    constructor(){
        super()
        this.state={
            styleMenu:true
        }
    }
    click=()=>{
        this.setState({
           styleMenu:!this.state.styleMenu
        })
    }
    render() {
        return (
        <div className={styles.navbar}>
         <Mydiv onClick={this.click} status={this.state.styleMenu}>
            <span></span>
            <span></span>
            <span></span>
            <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/biography">Biography</Link></li>
                <li><Link to="/bussines">Bussines</Link></li>
                <li><Link to="/entertainment">Entertainment</Link></li>
            </ul>   
         </Mydiv>
            <ul className={styles.list}>
                <li><Link to="/"  >Home</Link></li>
                <li><Link to="/biography">Biography</Link></li>
                <li><Link to="/bussines">Bussines</Link></li>
                <li><Link to="/entertainment">Entertainment</Link></li>
            </ul>
            <Link to="/"><img className={styles.img_logo} src={logo} alt='Logo'/></Link> 
            </div>
        );
    }
}

export {Navbar};
