import React,{ useEffect, useState} from 'react';
import misozam from "./masozam.mp3";
import GTA from "./GTA.mp3";
import Maria from "./Maria.mp3";
import SHAYATEEN from "./SHAYATEEN.mp3";
import styles from "./musicPLY.module.css"
import play from "./pic/bx-play.svg" 
import pause from "./pic/bx-pause.svg" 
import { Link } from 'react-router-dom';
 
const data=[
    {
        id:1,
        title:"The-AB7",
        name:"GTA",
        src:GTA,
        poster:"https://i1.sndcdn.com/artworks-zNMOLtAABQUsR253-syMUBw-t500x500.jpg"
    },
    {
        id:2,
        title:"P6rhum",
        name:"Maria",
        src:Maria,
        poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
    },
    {
        id:3,
        title:"DIXR",
        name:"SHAYATEEN",
        src:SHAYATEEN,
        poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
    },
    {
        id:4,
        title:"HipHopologist",
        name:"Capsule Albume (Misoozam)",
        src:misozam,
        poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
    },
    {
        id:5,
        title:"GodPoori",
        name:"SHOOTIA",
        src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
        poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
    }
]



const MusicPLY = (props) => {
 let Music=document.getElementsByTagName("audio");
 const [Data]=useState(data);
 const [playing,setplaying]=useState(false)
 const id=props.match.params.id
 const itemPlay =Data[id-1]
 const srcItem=itemPlay.src
 useEffect(()=>{ 
   console.log(Music[0].loop=true)
   const playAudio=()=>{
    Music[0].play()
   }
   playAudio()
 },[Music,Data,id,itemPlay])
const changeplay=()=>{
    if(playing===false){

    }
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" style="fill: rgba(0, 0, 0, 1);transform: ;msFilter:;"><path d="M12 2C6.486 2 2 6.486 2 12s4.486 10 10 10 10-4.486 10-10S17.514 2 12 2zm0 18c-4.411 0-8-3.589-8-8s3.589-8 8-8 8 3.589 8 8-3.589 8-8 8z"></path><path d="M13 9h2v6h-2zM9 9h2v6H9z"></path></svg>
}
return(<div className={styles.conteiner}>
    <div className={styles.play}  >
<audio src={srcItem} />
               <img alt="my-music" src={itemPlay.poster} style={{margin:"1.5rem",borderRadius:"30px"}}/>
               <div style={{display:"flex" , justifyContent:"center",position:"absolute",top:"270px",left:"210px"}}>
                  <img alt="icon" onClick={changeplay}  style={{border:"5px solid black",width:"85px",opacity:"0.55",backgroundColor:"rgb(54, 252, 209)",borderRadius:"50%",margin:"1.5rem"}} src={playing?play:pause}  />
</div>
</div>
<div>   
        <div className={styles.conteinerList}>
        {Data.map((e)=><div className={styles.music}>
        <Link to={`/Entertainment/playmusic/${e.id}`}><img alt='poster' src={e.poster} style={{width:"95px",borderRadius:"10px"}}/></Link>
    <div className={styles.text}>
    <Link to={`/Entertainment/playmusic/${e.id}`}><h2>{e.title}</h2></Link>
<h3>{e.name}</h3>
    </div>
</div>)}
        </div>

</div>
</div>)
}

export default MusicPLY;
