import React, { Component } from 'react';
import negative from "../picture/negative.svg"
import positive from "../picture/positive.svg"

class Main extends Component {
    constructor(){
        super();
        this.state={
            o:0
        }
    }
    up(){
        this.setState((p)=>{
            p.o+=1
        })
    }
    down(){
       if(this.state.o=0){
        alert("مقدار صفر است .")
       }else{
        this.setState((p)=>{
            p.o-=1
        })
       }
    }
    render() {
        return (
            <div>
                   <div className={styles.Main}>
            <img src={this.props.picture} alt='billboard'/>
