        super();
        this.state={
            o:0
        }
    }
    down=()=>{
      if(this.state.o=0){
        alert("مقدار صفر است .")
      }else{
        this.setState(p=>{
            p.o-=1
        })
      }
       
    }
    up=()=>{
        if(this.state.o=5){
          alert("بیش از این نمیتوانید انتخاب کنید")
        }else{
          this.setState(p=>{
              p+=1
          })
        }
         
      }
