import React,{  useState} from 'react';
import {Link}from "react-router-dom"
 import shootia from "./gooz.mp3";
import pause from "./pic/bx-pause.svg" 
import play from "./pic/bx-play.svg" 
import left from "./pic/bx-left.svg" 
import right from "./pic/bx-right.svg" 
const data=[
    {
        id:1,
        title:"P6Savage",
        name:"Driver By (FreeStyle)",
        poster:shootia
    },
    {
        id:2,
        title:"P6rhum",
        name:"Maria",
        poster:"https://i1.sndcdn.com/artworks-SZ6WXOUXkw8Dtd7k-QT1lbQ-t500x500.jpg"
    },
    {
        id:3,
        title:"DIXR",
        name:"SHAYATEEN",
        poster:"https://i1.sndcdn.com/artworks-xzac5Alaw31tGyFL-cyhgqA-t500x500.jpg"
    },
    {
        id:4,
        title:"HipHopologist",
        name:"Capsule Albume",
        poster:"https://i1.sndcdn.com/artworks-IyXJUUiehrG7Eljy-jjqIDw-t500x500.jpg"
    },
    {
        id:5,
        title:"GodPoori",
        name:"SHOOTIA",
        src:"https://ups.music-fa.com/tagdl/8e401/Godpoori%20-%20Shotia%20(320).mp3",
        poster:"https://i1.sndcdn.com/artworks-2Fqdz2rt0TzkAwKl-iEnVQw-t500x500.jpg"
    }
]



const MusicPLY = (props) => {
 const Music=document.getElementsByTagName("audio") 
 const [Data]=useState(data);
 const id =props.match.params.id;
 const [playing,setPlaying]=useState(false)
 const item=Data[id-1]
 const srcItem=item.src
 const MyMusic=Music.current;

 const playAudio=()=>{
    console.log(MyMusic)
 }
 const pauseAudio=()=>{
    MyMusic.pause()
 }
const channgeplaying=()=>{
    if(playing===false){
        playAudio();
        setPlaying(!playing)

    }
    if(playing===true){
        pauseAudio()
        setPlaying(!playing)
    }
}
    return (
        <>
            <div>
               <audio ref={Music} src={srcItem}/>
               <img alt="my-music" src={item.poster}/>
               <div style={{display:"flex"}}>
                  <img alt="icon" style={{width:"45px"}} src={left} />
                  <img alt="icon" onClick={channgeplaying} style={{width:"45px"}} src={playing?pause:play}  />
             <Link to={`/Entertainment/${Number(id)+1}`}><img alt="icon" style={{width:"45px"}} src={right} /></Link>  
               </div>
                </div>
            </>
    );
};

export default MusicPLY
